import React from 'react';
import { array } from 'prop-types';



  

export default class CardUI extends React.Component{
 

  

state={
imgBackground:'Normal.jpeg',
title:'',
starsState:'',
description:'',
attack:'',
defense:'',
year:'',
firstedition:'checked'

}

starsdata=(event)=>{
 
  this.setState({starsState:event.target.value});
 var StarsVariable= event.target.value;
  
var calculateDisplay= 

(StarsVariable==0)?'0':
(StarsVariable==1)?'1':
(StarsVariable==2)?'2': 

(StarsVariable==3)?'3':

'4';



alert(calculateDisplay);
  }

BGchange=(event)=>{

(event.value="Normal")?
this.setState(this.state.imgBackground='../Images/Normal.jpg'):
this.setState(this.state.imgBackground="a")

}


render(){
return(
    <div>
     <select name="Background">
    <option onchange={this.BGchange} value="Normal">Normal</option>
    <option onchange={this.BGchange} value="Special">Special</option>
    <option onchange={this.BGchange}value="Support">Support</option>
    <option onchange={this.BGchange} value="Ritual">Ritual</option>
    <option onchange={this.BGchange} value="Obelisk">Obelisk</option>
    <option onchange={this.BGchange} value="Ra">Ra</option>
  </select>
    
    <input id="stars" type="number" max="5" onChange={this.starsdata}></input><h1>{this.state.starsState}</h1>
 
    <input type="file"></input>
    <input type="text" maxLength="50"></input>
    <label>Attack</label><input type="number"></input>
    <label>Defense</label><input type="number"></input>
    <label>Year</label><input type="number"></input>
    <input type="checkbox"></input>
    




<div>



<img id="cardBg" src={this.state.imgBackground}></img>
<h3 id="Title">{this.props.title}</h3>
<div><img id="stars" src=""/></div>
<img id="poster" src=""/>
<p id="description"></p>
<p id="attack"></p>
<p id="defense"></p>





</div>
</div>
)

}


}
