import React from 'react';
import { Normal } from '../Images/Normal.jpg';


  

export default class CardUI extends React.Component{
 

  

state={
imgBackground:'',
title:'',
starsState:'',
description:'',
attack:'',
defense:'',
year:'',
firstedition:'checked',
 selectValue:''
  
}

starsdata=(event)=>{
 
  this.setState({starsState:event.target.value});
 var StarsVariable= event.target.value;
  
var calculateDisplay= 

(StarsVariable==0)?'0':
(StarsVariable==1)?'1':
(StarsVariable==2)?'2': 

(StarsVariable==3)?'3':

'4';



alert(calculateDisplay);
  }

  getInitialState=()=>{
    
    
  }
 
handleChange=(e)=>{
  this.setState({selectValue:e.target.value});

  (this.state.selectValue="Normal")?
this.setState(imgBackground=Normal):
"a"


}


render(){
return(
    <div>
     <select id="bgOption" value={this.state.selectValue} onChange={this.handleChange} name="Background">
       <option value="please select">Please select</option>
    <option value="Normal">Normal</option>
    <option value="Special">Special</option>
    <option value="Support">Support</option>
    <option value="Ritual">Ritual</option>
    <option value="Obelisk">Obelisk</option>
    <option value="Ra">Ra</option>
  </select>
    
    <input id="stars" type="number" max="5" onChange={this.starsdata}></input><h1>{this.state.starsState}</h1>
 
    <input type="file"></input>
    <input type="text" maxLength="50"></input>
    <label>Attack</label><input type="number"></input>
    <label>Defense</label><input type="number"></input>
    <label>Year</label><input type="number"></input>
    <input type="checkbox"></input>
    




<div>



<img id="cardBg" src={this.state.imgBackground}></img>
<h3 id="Title">{this.props.title}</h3>
<div><img id="stars" src=""/></div>
<img id="poster" src=""/>
<p id="description"></p>
<p id="attack"></p>
<p id="defense"></p>





</div>
</div>
)

}


}
