import React from 'react';
import Normal from '../Images/Normal.jpg';
import Spell from '../Images/Spell.png';
import Ritual from '../Images/Ritual.png';
import Trap from '../Images/Trap.png';
import Special from '../Images/Special.png';
import Stars from '../Images/Star.png';


export default class CardUI extends React.Component{
 

  componentDidUpdate(){
console.log("updated")

  }
  
  

state={
  imgBackground:Normal,
title:'',
starsState:'',
description:'',
attack:'',
defense:'',
year:'',
firstedition:'checked',
value:''

}

starsdata=(event)=>{
 
  this.setState({starsState:event.target.value});
 var StarsVariable= event.target.value;
  
var calculateDisplay= 

(StarsVariable===0)?'0':
(StarsVariable===1)?'1':
(StarsVariable===2)?'2': 

(StarsVariable===3)?'3':

'4';



alert(calculateDisplay);
  }

  
 
handleChange=(e)=>{
//GET THE VALUE OF THE OPTIONS
this.setState({value:e.target.value});



}



submit=(e)=>{
  e.preventDefault();
  console.log(this.state.value);



  if(this.state.value==="Normal"){
    this.setState({imgBackground:Normal})
  
  }
  else if(this.state.value==="Spell"){
    this.setState({imgBackground:Spell})
  }
  else if(this.state.value==="Ritual"){
    this.setState({imgBackground:Ritual})
  }
  else if(this.state.value==="Trap"){
    this.setState({imgBackground:Trap})
  }
  else if(this.state.value==="Special"){
    this.setState({imgBackground:Special})
  }
}

render(){
return(
    <div>
      <form onSubmit={this.submit}>
     <select onChange={this.handleChange} className="Select">

<option value="Normal">Normal</option>


<option value="Special">Special</option>


<option value="Trap">Trap</option>


<option value="Ritual">Ritual</option>


<option value="Spell">Spell</option>
     </select>

     
    
    <label>Level</label><input id="stars" type="number" max="5" onChange={this.starsdata}></input>
 
    <input type="file"></input>
    <input type="text" maxLength="50"></input>
    <label>Attack</label><input type="number"></input>
    <label>Defense</label><input type="number"></input>
    <label>Year</label><input type="number"></input>
    <input type="checkbox"></input>
    <button>Submit</button>
    </form>








{




(this.state.value==="Normal")?<img id="cardBg" className="cardBG" src={this.state.imgBackground}/>:
(this.state.value==="Ritual")?<img id="cardBg" className="cardBG" src={this.state.imgBackground}/>:
(this.state.value==="Spell")?<img id="cardBg" className="cardBG" src={this.state.imgBackground}/>:
(this.state.value==="Special")?<img id="cardBg" className="cardBG" src={this.state.imgBackground}/>:
<img id="cardBg" className="cardBG" src={this.state.imgBackground}/>


}
<h3 id="Title">{this.props.title}</h3>
{

(this.state.value==="1")?<img id="stars" class="stars" src={Stars}/>:
(this.state.value==="2")?<div><img id="stars" class="stars" src={Stars}/><img id="stars" class="stars" src={Stars}/></div>:
(this.state.value==="3")?<div><img id="stars" class="stars" src={Stars}/><img id="stars" class="stars" src={Stars}/><img class="stars" id="stars" src={Stars}/></div>:
(this.state.value==="4")?<div><img id="stars" class="stars" src={Stars}/><img class="stars" id="stars" src={Stars}/><img class="stars" id="stars" src={Stars}/><img id="stars" class="stars" src={Stars}/></div>:
(this.state.value==="5")?<div><img id="stars" class="stars" src={Stars}/><img id="stars" class="stars" src={Stars}/><img class="stars" id="stars" src={Stars}/><img class="stars" id="stars" src={Stars}/><img id="stars" class="stars" src={Stars}/></div>:
<div></div>
  }
<img id="poster" src=""/>
<p id="description"></p>
<p id="attack"></p>
<p id="defense"></p>





</div>


)}}
