import React from 'react';
import Normal from '../Images/Normal.jpg';
import Spell from '../Images/Spell.png';






export default class CardUI extends React.Component{
 
  
  componentDidUpdate(){
console.log("updated")

  }
  
  

state={
  imgBackground:Normal,
title:'',
starsState:'',
description:'',
attack:'',
defense:'',
year:'',
firstedition:'checked',
value:''

}

starsdata=(event)=>{
 
  this.setState({starsState:event.target.value});
 var StarsVariable= event.target.value;
  
var calculateDisplay= 

(StarsVariable===0)?'0':
(StarsVariable===1)?'1':
(StarsVariable===2)?'2': 

(StarsVariable===3)?'3':

'4';



alert(calculateDisplay);
  }

  
 
handleChange=(e)=>{
//GET THE VALUE OF THE OPTIONS
this.setState({value:e.target.value});



}



submit=(e)=>{
  e.preventDefault();
  console.log(this.state.value);



  if(this.state.value="Normal"){
    this.setState({imgBackground:Normal})
  
  }
  else if(this.state.value="Spell"){
    this.setState({imgBackground:Spell})
  }
  else{
    alert("nope")
  }
  
}

render(){
return(
    <div>
      <form onSubmit={this.submit}>
     <select onChange={this.handleChange} className="Select">

<option value="Normal">Normal</option>


<option value="Ritual">Special</option>

<option value="Ritual">Ritual</option>


<option value="Spell">Spell</option>
     </select>

     
    
    <input id="stars" type="number" max="5" onChange={this.starsdata}></input><h1>{this.state.starsState}</h1>
 
    <input type="file"></input>
    <input type="text" maxLength="50"></input>
    <label>Attack</label><input type="number"></input>
    <label>Defense</label><input type="number"></input>
    <label>Year</label><input type="number"></input>
    <input type="checkbox"></input>
    <button>Submit</button>
    </form>




<div>



{




/*(this.state.value="Normal")?<img id="cardBg" className="cardBG" src={this.state.imgBackground}></img>:
(this.state.value="Ritual")?<img id="cardBg" className="cardBG" src={this.state.imgBackground}></img>:
(this.state.value="Spell")?<img id="cardBg" className="cardBG" src={this.state.imgBackground}></img>:
(this.state.value="Special")?*/
<img id="cardBg" className="cardBG" src={this.state.imgBackground}/>


}
<h3 id="Title">{this.props.title}</h3>
<div><img id="stars" src=""/></div>
<img id="poster" src=""/>
<p id="description"></p>
<p id="attack"></p>
<p id="defense"></p>





</div>
</div>
)

}


}
