import React from 'react';
import './App.css';


  

class CardUI extends React.Component{
 

state={
imgBackground:'',
title:'',
starsState:'0',
description:'',
attack:'',
defense:'',
year:'',
firstedition:!this.state.firstedition,
}

starsdata=()=>{
 
  this.setState({starsState:event.target.value});
 if(starsState == 0)
 {
alert({starsState});

 }     
  }
render(){
return(
    <div>
     <select name="Background">
    <option value="Normal">Normal</option>
    <option value="Special">Special</option>
    <option value="Support">Support</option>
    <option value="Ritual">Ritual</option>
    <option value="Obelisk">Obelisk</option>
    <option value="Ra">Ra</option>
  </select>
    
    <input id="stars" type="number" value={this.state.starsState} max="5" onchange={this.starsdata}></input>
    <div><button>prev</button><p>Card Name</p><button>next</button></div>
    <input type="file"></input>
    <input type="text" maxLength="50"></input>
    <label>Attack</label><input type="number"></input>
    <label>Defense</label><input type="number"></input>
    <label>Year</label><input type="number"></input>
    <input type="checkbox"></input>
    

<img src={this.state.imgBackground}/>
<p>{this.state.stars}</p>
</div>
)}

}
