import React, { useState } from 'react';
import './App.css';
import CardUI from './Components/CardUI';




function App() {

  const [image, setImage] = useState('')
  const [loading, setLoading] = useState(false)


  const fileUploadHandler= async e =>{

    const files= e.target.file

    const data = new FormData

    data.append('file',files[0]);

    data.append('upload_preset', 'di5bibokq')
    setLoading(true)

    const res = await fetch('https://api.cloudinary.com/v1_1/di5bibokq',{
    method:'POST',
    body:data
    })

    const file= await res.json()


    setImage(file.secure_url)
    setLoading(false)

    }
  return (


    <div className="App">
      <header className="App-header">
        <h1>Yugioh Card Generator</h1>
        <CardUI/>
      </header>
      <input type="file" onChange={fileUploadHandler} placeholder="Add Image"/>
      {loading?(<h3>Loading</h3>):(<img src={{image}} style={{ width: '300px' }}/>)}

    </div>
  );
}

export default App;
